    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}connexion{% endblock %}

{% block body %}

    <header class=\"container-fluid p-5 text-center bg-light\">
        <h1>Connexion</h1>
    </header>

    <main class=\"conatiner-fluid\">

        <div class=\"row\">

            <form method=\"post\"class=\"col-4 offset-4 my-5\">

                {% if error %}
                    <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
                {% endif %}
    
                {% if app.user %}
                    <div class=\"mb-3\">Vous êtes connecté en tant que {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">déconnexion</a></div>
                {% endif %}
    
                <div class=\"mb-3\">
                    <label for=\"inputEmail\">Email</label>
                    <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\" autocomplete=\"email\" required autofocus>    
                </div>
                
                <div class=\"mb-3\">
                    <label for=\"inputPassword\">Mot de passe</label>
                    <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" autocomplete=\"current-password\" required>
                </div>

                <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">
    
                <div class=\"checkbox mb-3\">
                    <label>
                        <input type=\"checkbox\" name=\"_remember_me\"> se souvenir de moi
                    </label>
                </div>
    
                <div class=\"d-flex justify-content-end\">
                    <button class=\"btn btn-primary\" type=\"submit\">Connexion</button>
                </div>
                
            </form>

        </div>

    </main>

{% endblock %}", "security/login.html.twig", "C:\\wamp64\\www\\symfony\\sublimmo\\templates\\security\\login.html.twig");
